var _ = require('underscore');

var Cassandra = require('oae-util/lib/cassandra');
var log = require('oae-logger').logger('oae-activity');

/**
 * TODO bla bla bla
 */
let getTenantLogo = function(tenantAlias, callback) {

    Cassandra.runQuery('SELECT "value" FROM "Config" where "tenantAlias" = ?', [tenantAlias], function(err, rows) {
        if (err) {
            // TODO log here
            return callback(err);
        }

        if(rows.length < 1) {
            // TODO log here
            return callback(null, '');
        } else {
            let tenantLogo = JSON.parse(_.first(rows).get('value'))['institutional-logo-url'];
            return callback(null, tenantLogo);
        }
    });
};

 module.exports = {
     'getTenantLogo': getTenantLogo
 };
